mapper.update:
UPDATE ai_advisor
advisor_id = #{advisorId}, advisor_name = #{advisorName}, advisor_type = #{advisorType}, advisor_desc = #{advisorDesc}, advisor_order = #{advisorOrder}, advisor_param = #{advisorParam}, advisor_status = #{advisorStatus},
WHERE id = #{id}

UPDATE ai_advisor
SET advisor_status = #{advisorStatus}
WHERE id = #{id}
