mapper.select:
SELECT id, agent_id, agent_name, agent_type, agent_desc, agent_status, create_time, update_time
FROM ai_agent
WHERE agent_id = #{agentId}

SELECT id, agent_id, agent_name, agent_type, agent_desc, agent_status, create_time, update_time
FROM ai_agent
WHERE id = #{id}

SELECT id, agent_id, agent_name, agent_type, agent_desc, agent_status, create_time, update_time
FROM ai_agent


        AND (agent_id LIKE CONCAT('%', #{keyword}, '%')
            OR agent_name LIKE CONCAT('%', #{keyword}, '%'))


        AND agent_status = #{agentStatus}


        AND agent_type = #{agentType}


ORDER BY id DESC

    LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ai_agent


        AND (agent_id LIKE CONCAT('%', #{keyword}, '%')
            OR agent_name LIKE CONCAT('%', #{keyword}, '%'))


        AND agent_status = #{agentStatus}


        AND agent_type = #{agentType}

mapper.update:
UPDATE ai_agent
agent_id = #{agentId}, agent_name = #{agentName}, agent_type = #{agentType}, agent_desc = #{agentDesc}, agent_status = #{agentStatus},
WHERE id = #{id}
