mapper.select:
SELECT id, api_id, api_base_url, api_key, api_completions_path, api_embeddings_path, api_status, create_time, update_time FROM ai_api
AND (api_id LIKE CONCAT('%', #{keyword}, '%') OR api_base_url LIKE CONCAT('%', #{keyword}, '%'))
AND api_status = #{status}
ORDER BY id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1) FROM ai_api
AND (api_id LIKE CONCAT('%', #{keyword}, '%') OR api_base_url LIKE CONCAT('%', #{keyword}, '%'))
AND api_status = #{status}

mapper.update:
UPDATE ai_api
api_id = #{apiId}, api_base_url = #{apiBaseUrl}, api_key = #{apiKey}, api_completions_path = #{apiCompletionsPath}, api_embeddings_path = #{apiEmbeddingsPath}, api_status = #{apiStatus},
WHERE id = #{id}
