mapper.select:
SELECT id, client_id, client_type, client_role, model_id, model_name, client_name, client_desc, client_status, create_time, update_time FROM ai_client
AND client_id LIKE CONCAT('%', #{idKeyword}, '%')
AND client_name LIKE CONCAT('%', #{nameKeyword}, '%')
AND model_id = #{modelId}
AND client_type = #{clientType}
AND client_role = #{clientRole}
ORDER BY update_time DESC, id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ai_client


        AND client_id LIKE CONCAT('%', #{idKeyword}, '%')


        AND client_name LIKE CONCAT('%', #{nameKeyword}, '%')


        AND model_id = #{modelId}


        AND client_type = #{clientType}


        AND client_role = #{clientRole}

SELECT client_id
FROM ai_client
WHERE model_id = #{modelId}

mapper.update:
UPDATE ai_client
client_id = #{clientId}, client_type = #{clientType}, client_role = #{clientRole}, model_id = #{modelId}, model_name = #{modelName}, client_name = #{clientName}, client_desc = #{clientDesc}, client_status = #{clientStatus},
WHERE id = #{id}

UPDATE ai_client
SET client_status = #{clientStatus}
WHERE id = #{id}
