mapper.select:
SELECT id, client_id, client_type, model_id, model_name, client_name, client_desc, client_status, create_time, update_time FROM ai_client
AND (client_id LIKE CONCAT('%', #{keyword}, '%') OR client_name LIKE CONCAT('%', #{keyword}, '%'))
AND model_id = #{modelId}
AND client_type = #{clientType}
AND client_status = #{status}
ORDER BY id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ai_client


        AND (client_id LIKE CONCAT('%', #{keyword}, '%')
         OR client_name LIKE CONCAT('%', #{keyword}, '%'))


        AND model_id = #{modelId}


        AND client_type = #{clientType}


        AND client_status = #{status}

mapper.update:
UPDATE ai_client
client_id = #{clientId}, client_type = #{clientType}, model_id = #{modelId}, model_name = #{modelName}, client_name = #{clientName}, client_desc = #{clientDesc}, client_status = #{clientStatus},
WHERE id = #{id}

