mapper.select:
SELECT client_id
FROM ai_config
WHERE config_type = #{configType}

SELECT client_id
FROM ai_config
WHERE config_type = #{configType}
  AND config_value = #{configValue}

mapper.update:
UPDATE ai_config
client_id = #{clientId}, config_type = #{configType}, config_value = #{configValue}, config_param = #{configParam}, config_status = #{configStatus},
WHERE id = #{id}

UPDATE ai_config
SET config_status = #{status}
WHERE id = #{id}
