mapper.select:
SELECT id, agent_id, client_id, client_type, flow_prompt, flow_seq, flow_status, create_time, update_time FROM ai_flow
AND agent_id = #{agentId}
AND client_id = #{clientId}
AND flow_status = #{status}
ORDER BY id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ai_flow


        AND agent_id = #{agentId}


        AND client_id = #{clientId}


        AND flow_status = #{status}

