mapper.select:
SELECT agent_id
FROM ai_flow
WHERE client_id = #{clientId}

mapper.update:
UPDATE ai_flow
SET flow_prompt = #{flowPrompt}
WHERE client_id = #{clientId}

UPDATE ai_flow
agent_id = #{agentId}, client_id = #{clientId}, client_role = #{clientRole}, flow_prompt = #{flowPrompt}, flow_seq = #{flowSeq},
WHERE id = #{id}
