mapper.select:
SELECT id, mcp_id, mcp_name, mcp_type, mcp_config, mcp_desc, mcp_timeout, mcp_chat, mcp_status, create_time, update_time FROM ai_mcp
AND (mcp_id LIKE CONCAT('%', #{keyword}, '%') OR mcp_name LIKE CONCAT('%', #{keyword}, '%'))
AND mcp_type = #{mcpType}
AND mcp_status = #{status}
ORDER BY id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ai_mcp


        AND (mcp_id LIKE CONCAT('%', #{keyword}, '%')
          OR mcp_name LIKE CONCAT('%', #{keyword}, '%'))


        AND mcp_type = #{mcpType}


        AND mcp_status = #{status}

mapper.update:
UPDATE ai_mcp
mcp_id = #{mcpId}, mcp_name = #{mcpName}, mcp_type = #{mcpType}, mcp_config = #{mcpConfig}, mcp_desc = #{mcpDesc}, mcp_timeout = #{mcpTimeout}, mcp_chat = #{mcpChat}, mcp_status = #{mcpStatus},
WHERE id = #{id}
