mapper.select:
SELECT client_id
FROM ai_config
WHERE config_type = 'prompt' AND config_value = #{promptId}

SELECT id, prompt_id, prompt_name, prompt_content, prompt_desc, prompt_status, create_time, update_time FROM ai_prompt
AND (prompt_id LIKE CONCAT('%', #{keyword}, '%') OR prompt_name LIKE CONCAT('%', #{keyword}, '%'))
AND prompt_status = #{status}
ORDER BY id DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1) FROM ai_prompt
AND (prompt_id LIKE CONCAT('%', #{keyword}, '%') OR prompt_name LIKE CONCAT('%', #{keyword}, '%'))
AND prompt_status = #{status}

mapper.update:
UPDATE ai_prompt
prompt_id = #{promptId}, prompt_name = #{promptName}, prompt_content = #{promptContent}, prompt_desc = #{promptDesc}, prompt_status = #{promptStatus},
WHERE id = #{id}

UPDATE ai_prompt
SET prompt_content = #{promptContent}
WHERE prompt_id = #{promptId}
